import {
  Link,
  Links,
  LiveReload,
  Meta,
  Outlet,
  Scripts,
  ScrollRestoration,
  useCatch,
} from 'remix';

import globalStylesUrl from '~/styles/global.css';
import darkStylesUrl from '~/styles/dark.css';

// https://remix.run/api/app#links
export let links = () => {
  return [
    { rel: 'stylesheet', href: globalStylesUrl },
    {
      rel: 'stylesheet',
      href: darkStylesUrl,
      media: '(prefers-color-scheme: dark)',
    },
  ];
};

// https://remix.run/api/conventions#default-export
// https://remix.run/api/conventions#route-filenames
export default function App() {
  return (
    <Document>
      <Layout>
        <Outlet />
      </Layout>
    </Document>
  );
}

// https://remix.run/docs/en/v1/api/conventions#errorboundary
export function ErrorBoundary({ error }) {
  console.error(error);
  return (
    <Document title='Error!'>
      <Layout>
        <div>
          <h1>There was an error</h1>
          <p>{error.message}</p>
        </div>
      </Layout>
    </Document>
  );
}

// https://remix.run/docs/en/v1/api/conventions#catchboundary
export function CatchBoundary() {
  let caught = useCatch();

  let message;
  switch (caught.status) {
    case 401:
      message = <p>401 you dont have access to this page.</p>;
      break;
    case 404:
      message = <div style={{ display: 'flex', justifyContent: 'center' }}>
        <img 
        src="/page-not-found.gif" 
        alt="404" 
        width='450'
        />
      </div>;
      break;

    default:
      throw new Error(caught.data || caught.statusText);
  }

  return (
    <Document title={`${caught.status} ${caught.statusText}`}>
      <Layout>
        <h1>
          {caught.status}: {caught.statusText}
        </h1>
        {message}
      </Layout>
    </Document>
  );
}

function Document({ children, title }) {
  return (
    <html lang='en'>
      <head>
        <meta charSet='utf-8' />
        <meta name='viewport' content='width=device-width,initial-scale=1' />
        {title ? <title>{title}</title> : null}
        <Meta />
        <Links />
      </head>
      <body>
        {children}
        <ScrollRestoration />
        <Scripts />
        {process.env.NODE_ENV === 'development' && <LiveReload />}
      </body>
    </html>
  );
}

function Layout({ children }) {
  return (
    <div className='remix-app'>
      <header className='remix-app__header'>
        <div className='container remix-app__header-content'>
          <Link to='/' title='Logo' className='remix-app__header-home-link'>
            <Logo />
          </Link>
          <nav aria-label='Main navigation' className='remix-app__header-nav'>
            <ul>
              <li>
                <a href='https://github.com/cenamax'>GitHub</a>
              </li>
              <li>
                <Link to='/about'>About</Link>
              </li>
            </ul>
          </nav>
        </div>
      </header>
      <div className='remix-app__main'>
        <div className='container remix-app__main-content'>{children}</div>
      </div>
      <footer className='remix-app__footer'>
        <div className='container remix-app__footer-content'>
          <p>
            Happy Coding ! - <a href='https://github.com/cenamax'>CenaMaX</a> -
          </p>
        </div>
      </footer>
    </div>
  );
}

